import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes, faSun, faMoon } from '@fortawesome/free-solid-svg-icons';
import './Header.css';

const Header = ({ activeSection, isDarkMode, toggleTheme }) => {
  const { t, i18n } = useTranslation();
  const [showMenu, setShowMenu] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  // Lista de idiomas disponibles
  const languages = [
    { code: 'es', label: 'ES' },
    { code: 'en', label: 'EN' }
  ];

  // Cambiar idioma
  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  // Controlar scroll
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 50;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [scrolled]);

  // Manejar clic en enlace de navegaciÃ³n
  const handleNavLinkClick = (sectionId) => {
    setShowMenu(false);
    document.getElementById(sectionId).scrollIntoView({ behavior: 'smooth' });
  };

  // Animaciones
  const navVariants = {
    hidden: { opacity: 0, x: '100%' },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        type: 'spring',
        stiffness: 80,
        damping: 20
      }
    },
    exit: {
      opacity: 0,
      x: '100%',
      transition: {
        type: 'spring',
        stiffness: 100,
        damping: 20
      }
    }
  };

  const logoVariants = {
    normal: { scale: 1 },
    hover: { 
      scale: 1.05,
      transition: { 
        type: 'spring', 
        stiffness: 300, 
        damping: 10 
      }
    }
  };

  return (
    <header className={`header ${scrolled ? 'scrolled' : ''}`}>
      <div className="container header-container">
        <motion.div 
          className="logo" 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <motion.a 
            href="#inicio" 
            variants={logoVariants}
            initial="normal"
            whileHover="hover"
            whileTap={{ scale: 0.9 }}
          >
            <img src="/assets/logo-dm.png" alt="Diego Moreno" className="logo-img" />
            <span className="logo-text">Diego Moreno</span>
          </motion.a>
        </motion.div>

        <div className="nav-toggle">
          <motion.button 
            className="theme-toggle"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.1 }}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={toggleTheme}
            aria-label={isDarkMode ? 'Cambiar a modo claro' : 'Cambiar a modo oscuro'}
          >
            <FontAwesomeIcon icon={isDarkMode ? faSun : faMoon} />
          </motion.button>

          <motion.button 
            className="menu-toggle"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={() => setShowMenu(!showMenu)}
            aria-expanded={showMenu}
            aria-label="MenÃº de navegaciÃ³n"
          >
            <FontAwesomeIcon icon={showMenu ? faTimes : faBars} />
          </motion.button>
        </div>

        <AnimatePresence>
          {(showMenu || window.innerWidth > 768) && (
            <motion.nav 
              className={`nav ${showMenu ? 'show' : ''}`}
              variants={navVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <ul className="nav-links">
                {['inicio', 'sobre-mi', 'habilidades', 'proyectos', 'contacto'].map((sectionId) => (
                  <motion.li 
                    key={sectionId}
                    initial={{ opacity: 0, y: -20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.2 + (0.1 * ['inicio', 'sobre-mi', 'habilidades', 'proyectos', 'contacto'].indexOf(sectionId)) }}
                  >
                    <a 
                      href={`#${sectionId}`}
                      onClick={(e) => {
                        e.preventDefault();
                        handleNavLinkClick(sectionId);
                      }}
                      className={activeSection === sectionId ? 'active' : ''}
                    >
                      {t(`nav.${sectionId}`)}
                      {activeSection === sectionId && (
                        <motion.div 
                          className="nav-indicator"
                          layoutId="navIndicator"
                          transition={{
                            type: 'spring',
                            stiffness: 200,
                            damping: 20
                          }}
                        />
                      )}
                    </a>
                  </motion.li>
                ))}
              </ul>

              <div className="language-switcher">
                {languages.map((lang) => (
                  <motion.button
                    key={lang.code}
                    onClick={() => changeLanguage(lang.code)}
                    className={i18n.language === lang.code ? 'active' : ''}
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    {lang.label}
                  </motion.button>
                ))}
              </div>
            </motion.nav>
          )}
        </AnimatePresence>
      </div>
    </header>
  );
};

export default Header;
