import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useTranslation } from 'react-i18next';
import Header from './components/header/Header';
import Hero from './components/hero/Hero';
import About from './components/about/About';
import Skills from './components/skills/Skills';
import Projects from './components/projects/Projects';
// import Contact from './components/contact/Contact';
import Footer from './components/footer/Footer';
import './App.css';
import './i18n/i18n';

function App() {
  const { i18n } = useTranslation();
  const [activeSection, setActiveSection] = useState('home');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Aplicar el idioma preferido del usuario al cargar
  useEffect(() => {
    const storedLanguage = localStorage.getItem('preferredLanguage');
    if (storedLanguage) {
      i18n.changeLanguage(storedLanguage);
    }
  }, [i18n]);

  // Aplicar tema preferido del usuario al cargar
  useEffect(() => {
    const storedTheme = localStorage.getItem('preferredTheme');
    const prefersDarkMode = window.matchMedia('(prefers-color-scheme: dark)').matches;
    const isDarkTheme = storedTheme === 'dark' || (storedTheme === null && prefersDarkMode);
    document.documentElement.classList.toggle('dark-theme', isDarkTheme);
    document.body.classList.toggle('dark-theme', isDarkTheme);
  }, []);

  // Detectar la sección activa al hacer scroll
  useEffect(() => {
    const handleScroll = () => {
      const sections = ['home', 'about', 'skills', 'projects']; // Eliminamos 'contact'

      // Encontrar la sección más cercana al viewport
      const current = sections.reduce((acc, section) => {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          const offset = Math.abs(rect.top);

          if (acc === null || offset < acc.offset) {
            return { section, offset };
          }
        }
        return acc;
      }, null);

      if (current) {
        setActiveSection(current.section);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Abrir/cerrar sidebar
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
    // Bloquear/desbloquear scroll del body cuando el sidebar está abierto
    document.body.style.overflow = !isSidebarOpen ? 'hidden' : '';
  };

  // Variantes para animaciones de secciones
  const sectionVariants = {
    hidden: {
      opacity: 0,
      y: 20
    },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <div className="portfolio">
      <Header 
        activeSection={activeSection} 
        isSidebarOpen={isSidebarOpen} 
        toggleSidebar={toggleSidebar}
      />

      <main className={isSidebarOpen ? 'sidebar-open' : ''}>
        <motion.div 
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
          variants={sectionVariants}
        >
          <Hero />
        </motion.div>

        <motion.section 
          id="about"
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
          variants={sectionVariants}
        >
          <About />
        </motion.section>

        <motion.section 
          id="skills"
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
          variants={sectionVariants}
        >
          <Skills />
        </motion.section>

        <motion.section 
          id="projects"
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
          variants={sectionVariants}
        >
          <Projects />
        </motion.section>

        {/* Sección de contacto eliminada */}

        <Footer />
      </main>
    </div>
  );
}

export default App;
