import React, { useRef } from 'react';
import { useInView } from 'framer-motion';
import './Skills.css';

const SkillCard = ({ skill, isInView }) => {
  return (
    <div className={`skill-card ${isInView ? 'visible' : ''}`}>
      <div className="skill-icon-container">
        <span className="skill-icon">{skill.icon}</span>
      </div>
      <span className="skill-name">{skill.name}</span>
    </div>
  );
};

const Skills = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, margin: '-100px' });

  const technicalSkills = [
    { name: 'React', icon: '⚛️', color: '#61DAFB' },
    { name: 'JavaScript', icon: '🟨', color: '#F7DF1E' },
    { name: 'TypeScript', icon: '🟦', color: '#3178C6' },
    { name: 'Node.js', icon: '🟩', color: '#68A063' },
    { name: 'Firebase', icon: '🔥', color: '#FFCA28' },
    { name: 'MongoDB', icon: '🍃', color: '#47A248' }
  ];

  const professionalSkills = [
    { name: 'Trabajo en equipo', icon: '👥', color: '#6C5CE7' },
    { name: 'Comunicación', icon: '💬', color: '#00B894' },
    { name: 'Resolución de problemas', icon: '🔧', color: '#FDCB6E' },
    { name: 'Gestión del tiempo', icon: '⏱️', color: '#E84393' },
    { name: 'Adaptabilidad', icon: '🔄', color: '#0984E3' },
    { name: 'Liderazgo', icon: '🌟', color: '#FF7675' }
  ];

  return (
    <section id="skills" className="skills-section" ref={ref}>
      <div className="skills-container">
        <h2 className="section-title">Habilidades Técnicas</h2>
        <div className="skills-grid">
          {technicalSkills.map((skill, index) => (
            <SkillCard 
              key={index} 
              skill={skill} 
              isInView={isInView} 
            />
          ))}
        </div>

        <h2 className="section-title">Habilidades Profesionales</h2>
        <div className="professional-skills">
          {professionalSkills.map((skill, index) => (
            <SkillCard 
              key={index} 
              skill={skill} 
              isInView={isInView} 
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
