import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGlobe, faCode, faGamepad, faChevronDown } from '@fortawesome/free-solid-svg-icons';
import { faGithub } from '@fortawesome/free-brands-svg-icons';
import './Projects.css';

// Importamos las imágenes (asegúrate de tener estas imágenes en tu carpeta public/assets/img)
const projectImages = {
  consum: '/assets/img/projects/consum.png',
  benidorm: '/assets/img/projects/benidorm.png',
  hm: '/assets/img/projects/hm.png',
  edificaciones: '/assets/img/projects/edificaciones.png',
  taskManager: '/assets/img/projects/task-manager.png',
  apiDotnet: '/assets/img/projects/api-dotnet.png',
  portfolio: '/assets/img/projects/portfolio.png',
  weatherApp: '/assets/img/projects/weather-app.png',
  unityGame: '/assets/img/projects/unity-game.png',
  wpfApp: '/assets/img/projects/wpf-app.png',
  mauiApp: '/assets/img/projects/maui-app.png'
};

const Projects = () => {
  const { t } = useTranslation();
  const [filter, setFilter] = useState('all');
  const [displayCount, setDisplayCount] = useState(6);

  // Datos de proyectos
  const projects = [
    {
      id: 1,
      key: 'consum',
      category: 'drupal',
      image: projectImages.consum,
      technologies: ['Drupal 9', 'PHP', 'JavaScript', 'API REST', 'SASS'],
      liveUrl: 'https://www.consum.es',
      githubUrl: '',
      featured: true
    },
    {
      id: 2,
      key: 'benidorm',
      category: 'drupal',
      image: projectImages.benidorm,
      technologies: ['Drupal 8', 'PHP', 'JavaScript', 'SASS', 'MySQL'],
      liveUrl: 'https://www.benidorm.org',
      githubUrl: '',
      featured: true
    },
    {
      id: 3,
      key: 'hm',
      category: 'drupal',
      image: projectImages.hm,
      technologies: ['Drupal 7/8', 'PHP', 'JavaScript', 'CSS3', 'jQuery'],
      liveUrl: 'https://www.hmhospitales.com',
      githubUrl: '',
      featured: false
    },
    {
      id: 4,
      key: 'edificaciones',
      category: 'wordpress',
      image: projectImages.edificaciones,
      technologies: ['WordPress', 'PHP', 'JavaScript', 'CSS', 'MySQL'],
      liveUrl: 'https://www.edificacionescaballero.es',
      githubUrl: '',
      featured: false
    },
    {
      id: 5,
      key: 'taskManager',
      category: 'angular',
      image: projectImages.taskManager,
      technologies: ['Angular', 'TypeScript', 'Bootstrap', 'Firebase', 'RxJS'],
      liveUrl: 'https://task-manager-dm.web.app',
      githubUrl: 'https://github.com/DM13250/task-manager',
      featured: true
    },
    {
      id: 6,
      key: 'apiDotnet',
      category: 'csharp',
      image: projectImages.apiDotnet,
      technologies: ['C#', '.NET Core', 'SQL Server', 'Swagger', 'Entity Framework'],
      liveUrl: 'https://api-demo.diegomoreno.dev',
      githubUrl: 'https://github.com/DM13250/dotnet-api',
      featured: false
    },
    {
      id: 7,
      key: 'portfolio',
      category: 'react',
      image: projectImages.portfolio,
      technologies: ['React', 'JavaScript', 'Framer Motion', 'i18next', 'CSS Modules'],
      liveUrl: 'https://diegomoreno.dev',
      githubUrl: 'https://github.com/DM13250/portfolio-react',
      featured: true
    },
    {
      id: 8,
      key: 'weatherApp',
      category: 'react',
      image: projectImages.weatherApp,
      technologies: ['React', 'OpenWeather API', 'ChartJS', 'CSS Grid', 'Axios'],
      liveUrl: 'https://weather-app-dm.web.app',
      githubUrl: 'https://github.com/DM13250/weather-app',
      featured: false
    },
    {
      id: 9,
      key: 'unityGame',
      category: 'unity',
      image: projectImages.unityGame,
      technologies: ['Unity', 'C#', '2D', 'Game Development'],
      liveUrl: 'https://unity-game-demo.diegomoreno.dev',
      githubUrl: 'https://github.com/DM13250/unity-game',
      featured: true
    },
    {
      id: 10,
      key: 'wpfApp',
      category: 'wpf',
      image: projectImages.wpfApp,
      technologies: ['WPF', 'C#', 'MVVM', 'XAML'],
      liveUrl: 'https://wpf-app-demo.diegomoreno.dev',
      githubUrl: 'https://github.com/DM13250/wpf-app',
      featured: false
    },
    {
      id: 11,
      key: 'mauiApp',
      category: 'maui',
      image: projectImages.mauiApp,
      technologies: ['MAUI', 'C#', 'XAML', 'Cross-platform'],
      liveUrl: 'https://maui-app-demo.diegomoreno.dev',
      githubUrl: 'https://github.com/DM13250/maui-app',
      featured: false
    }
  ];

  // Filtrado de proyectos
  const filteredProjects = filter === 'all' 
    ? [...projects].sort((a, b) => {
        if (a.featured && !b.featured) return -1;
        if (!a.featured && b.featured) return 1;
        return 0;
      })
    : projects.filter(project => project.category === filter);

  const displayedProjects = filteredProjects.slice(0, displayCount);
  const hasMoreProjects = displayCount < filteredProjects.length;

  const handleLoadMore = () => {
    setDisplayCount(prev => prev + 3);
  };

  // Reset display count when filter changes
  useEffect(() => {
    setDisplayCount(6);
  }, [filter]);

  // Animaciones
  const containerVariants = {
    hidden: {},
    visible: {
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const projectVariants = {
    hidden: {
      y: 30
    },
    visible: {
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <section id="projects" className="projects">
      <div className="projects-container">
        <div className="projects-title">
          <motion.h2
            initial={{ opacity: 0, y: -10 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true, amount: 0.2 }}
            transition={{ delay: 0.1, duration: 0.5 }}
            className="section-title"
          >
            {t('projects.title')}
          </motion.h2>
          <p className="section-subtitle">{t('projects.description')}</p>
        </div>

        <div className="filter-buttons">
          <button
            className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
            onClick={() => setFilter('all')}
          >
            {t('projects.filters.all')}
          </button>
          <button
            className={`filter-btn ${filter === 'drupal' ? 'active' : ''}`}
            onClick={() => setFilter('drupal')}
          >
            {t('projects.filters.drupal')}
          </button>
          <button
            className={`filter-btn ${filter === 'wordpress' ? 'active' : ''}`}
            onClick={() => setFilter('wordpress')}
          >
            {t('projects.filters.wordpress')}
          </button>
          <button
            className={`filter-btn ${filter === 'angular' ? 'active' : ''}`}
            onClick={() => setFilter('angular')}
          >
            {t('projects.filters.angular')}
          </button>
          <button
            className={`filter-btn ${filter === 'react' ? 'active' : ''}`}
            onClick={() => setFilter('react')}
          >
            {t('projects.filters.react')}
          </button>
          <button
            className={`filter-btn ${filter === 'csharp' ? 'active' : ''}`}
            onClick={() => setFilter('csharp')}
          >
            {t('projects.filters.csharp')}
          </button>
          <button
            className={`filter-btn ${filter === 'unity' ? 'active' : ''}`}
            onClick={() => setFilter('unity')}
          >
            {t('projects.filters.unity')}
          </button>
          <button
            className={`filter-btn ${filter === 'wpf' ? 'active' : ''}`}
            onClick={() => setFilter('wpf')}
          >
            {t('projects.filters.wpf')}
          </button>
          <button
            className={`filter-btn ${filter === 'maui' ? 'active' : ''}`}
            onClick={() => setFilter('maui')}
          >
            {t('projects.filters.maui')}
          </button>
        </div>

        <motion.div
          className="projects-grid"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
        >
          {displayedProjects.map(project => (
            <motion.div
              className="project-card" 
              key={project.id}
              variants={projectVariants}
            >
              <div className="project-image">
                <img src={project.image} alt={t(`projects.items.${project.key}.title`)} />
                {project.featured && <span className="featured-badge">{t('projects.common.featured')}</span>}
              </div>

              <div className="project-content">
                <h3 className="project-title">{t(`projects.items.${project.key}.title`)}</h3>
                <p className="project-description">{t(`projects.items.${project.key}.description`)}</p>
                <p className="project-detailed-description">{t(`projects.items.${project.key}.detailedDescription`)}</p>

                <div className="project-tech">
                  {project.technologies.map((tech, index) => (
                    <span key={index} className="tech-tag">{tech}</span>
                  ))}
                </div>

                <div className="project-links">
                  {project.liveUrl && (
                    <a href={project.liveUrl} target="_blank" rel="noopener noreferrer" className="project-link" title={t('projects.common.viewWebsite')}>
                      <FontAwesomeIcon icon={faGlobe} />
                    </a>
                  )}

                  {project.githubUrl && (
                    <a href={project.githubUrl} target="_blank" rel="noopener noreferrer" className="project-link" title={t('projects.common.viewGithub')}>
                      <FontAwesomeIcon icon={faGithub} />
                    </a>
                  )}
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {hasMoreProjects && (
          <motion.div
            className="load-more-container"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
          >
            <div 
              className="load-more-btn" 
              onClick={handleLoadMore}
              role="button"
              tabIndex={0}
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  handleLoadMore();
                }
              }}
            >
              {t('projects.common.loadMore')}
              <FontAwesomeIcon icon={faChevronDown} className="load-more-icon" />
            </div>
          </motion.div>
        )}
      </div>
    </section>
  );
};

export default Projects;
