import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';
import './Header.css';

const Header = () => {
  const { t } = useTranslation();
  const [scrolled, setScrolled] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  // Control de scroll para mostrar sombra en el header
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Animaciones
  const navLinksVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        staggerChildren: 0.1,
      }
    }
  };

  const navItemVariants = {
    hidden: { opacity: 0, y: -10 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <header className={`header ${scrolled ? 'scrolled' : ''}`}>
      <div className="container header-container">
        <a href="#inicio" className="logo">DM</a>
        
        <div className={`mobile-toggle ${mobileMenuOpen ? 'open' : ''}`} onClick={() => setMobileMenuOpen(!mobileMenuOpen)}>
          <span></span>
          <span></span>
          <span></span>
        </div>
        
        <motion.nav 
          className={`nav-menu ${mobileMenuOpen ? 'open' : ''}`}
          variants={navLinksVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.a href="#inicio" variants={navItemVariants}>
            {t('header.inicio')}
          </motion.a>
          <motion.a href="#sobremi" variants={navItemVariants}>
            {t('header.sobremi')}
          </motion.a>
          <motion.a href="#habilidades" variants={navItemVariants}>
            {t('header.skills')}
          </motion.a>
          <motion.a href="#proyectos" variants={navItemVariants}>
            {t('header.proyectos')}
          </motion.a>
          <motion.a href="#contacto" variants={navItemVariants}>
            {t('header.contacto')}
          </motion.a>
        </motion.nav>
      </div>
    </header>
  );
};

export default Header;
