import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faEnvelope, 
  faPhone, 
  faPaperPlane 
} from '@fortawesome/free-solid-svg-icons';
import { 
  faLinkedin, 
  faGithub, 
  faTwitter 
} from '@fortawesome/free-brands-svg-icons';
import './Contact.css';

const Contact = () => {
  const { t } = useTranslation();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });
  const [submitted, setSubmitted] = useState(false);
  const [formErrors, setFormErrors] = useState({});

  // Forzar visibilidad después de la carga
  useEffect(() => {
    const timer = setTimeout(() => {
      const contactContent = document.querySelector('.contact-content');
      const contactForm = document.querySelector('.contact-form');
      const contactInfo = document.querySelector('.contact-info');
      
      if (contactContent) contactContent.style.opacity = '1';
      if (contactForm) contactForm.style.opacity = '1';
      if (contactInfo) contactInfo.style.opacity = '1';
    }, 500);
    
    return () => clearTimeout(timer);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const validateForm = () => {
    let errors = {};
    let isValid = true;

    if (!formData.name.trim()) {
      errors.name = 'El nombre es obligatorio';
      isValid = false;
    }

    if (!formData.email.trim()) {
      errors.email = 'El email es obligatorio';
      isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      errors.email = 'El email no es válido';
      isValid = false;
    }

    if (!formData.message.trim()) {
      errors.message = 'El mensaje es obligatorio';
      isValid = false;
    }

    setFormErrors(errors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (validateForm()) {
      // Simular envío exitoso
      setTimeout(() => {
        setSubmitted(true);
        setFormData({
          name: '',
          email: '',
          subject: '',
          message: ''
        });
      }, 1000);
    }
  };

  // Animaciones
  const containerVariants = {
    hidden: { opacity: 0.5 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0.5, y: 15 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        type: 'spring',
        stiffness: 100,
        damping: 15,
        duration: 0.4
      }
    }
  };

  return (
    <section id="contacto" className="contact-section">
      <div className="container">
        <motion.div
          className="section-title"
          initial={{ opacity: 0.5, y: -10 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true, amount: 0.2 }}
          transition={{ delay: 0.1, duration: 0.3 }}
          style={{ opacity: 1 }}
        >
          <h2>{t('contacto.titulo')}</h2>
          <div className="underline"></div>
          <p className="section-subtitle">
            ¿Interesado en trabajar juntos o quieres saber más sobre mis proyectos? 
            Envíame un mensaje y me pondré en contacto contigo lo antes posible.
          </p>
        </motion.div>
        
        <motion.div 
          className="contact-content"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.2 }}
          style={{ opacity: 1 }}
        >
          <motion.div 
            className="contact-form-container"
            variants={itemVariants}
            style={{ opacity: 1 }}
          >
            {submitted ? (
              <div className="success-message">
                <FontAwesomeIcon icon={faPaperPlane} className="success-icon" />
                <h3>¡Mensaje enviado!</h3>
                <p>Gracias por contactarme. Te responderé lo antes posible.</p>
              </div>
            ) : (
              <form className="contact-form" onSubmit={handleSubmit}>
                <div className="form-group">
                  <label htmlFor="name">Nombre</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="Tu nombre"
                  />
                  {formErrors.name && <span className="error-message">{formErrors.name}</span>}
                </div>
                
                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    placeholder="Tu email"
                  />
                  {formErrors.email && <span className="error-message">{formErrors.email}</span>}
                </div>
                
                <div className="form-group">
                  <label htmlFor="subject">Asunto</label>
                  <input
                    type="text"
                    id="subject"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                    placeholder="Asunto del mensaje"
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="message">Mensaje</label>
                  <textarea
                    id="message"
                    name="message"
                    value={formData.message}
                    onChange={handleChange}
                    placeholder="Tu mensaje"
                    rows="5"
                  ></textarea>
                  {formErrors.message && <span className="error-message">{formErrors.message}</span>}
                </div>
                
                <button type="submit" className="submit-btn">
                  <FontAwesomeIcon icon={faPaperPlane} /> Enviar Mensaje
                </button>
              </form>
            )}
          </motion.div>
          
          <motion.div 
            className="contact-info"
            variants={itemVariants}
            style={{ opacity: 1 }}
          >
            <div className="contact-info-item">
              <FontAwesomeIcon icon={faEnvelope} className="contact-icon" />
              <div>
                <h4>Email</h4>
                <a href="mailto:info@diegomoreno.dev">info@diegomoreno.dev</a>
              </div>
            </div>
            
            <div className="contact-info-item">
              <FontAwesomeIcon icon={faPhone} className="contact-icon" />
              <div>
                <h4>Teléfono</h4>
                <a href="tel:+34600000000">+34 600 000 000</a>
              </div>
            </div>
            
            <div className="social-links">
              <h4>Redes Sociales</h4>
              <div className="social-icons">
                <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer" aria-label="LinkedIn">
                  <FontAwesomeIcon icon={faLinkedin} />
                </a>
                <a href="https://github.com" target="_blank" rel="noopener noreferrer" aria-label="GitHub">
                  <FontAwesomeIcon icon={faGithub} />
                </a>
                <a href="https://twitter.com" target="_blank" rel="noopener noreferrer" aria-label="Twitter">
                  <FontAwesomeIcon icon={faTwitter} />
                </a>
              </div>
            </div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Contact;
