import React, { useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import './Skills.css';

const Skills = () => {
  const skillsRef = useRef(null);

  const skillsData = [
    {
      title: 'Frontend',
      skills: [
        { name: 'React', icon: '⚛️', level: 90 },
        { name: 'JavaScript', icon: '🟨', level: 85 },
        { name: 'TypeScript', icon: '🟦', level: 80 },
        { name: 'HTML5', icon: '📄', level: 95 },
        { name: 'CSS3', icon: '🎨', level: 90 },
        { name: 'SASS', icon: '💅', level: 85 }
      ]
    },
    {
      title: 'Backend',
      skills: [
        { name: 'Node.js', icon: '🟩', level: 85 },
        { name: 'Express', icon: '🚀', level: 80 },
        { name: 'MongoDB', icon: '🍃', level: 75 },
        { name: 'Firebase', icon: '🔥', level: 80 },
        { name: 'REST API', icon: '🌐', level: 90 },
        { name: 'GraphQL', icon: '📊', level: 75 }
      ]
    },
    {
      title: 'Herramientas',
      skills: [
        { name: 'Git', icon: '🐙', level: 90 },
        { name: 'Webpack', icon: '📦', level: 75 },
        { name: 'Docker', icon: '🐳', level: 70 },
        { name: 'Jest', icon: '🧪', level: 80 },
        { name: 'Figma', icon: '✏️', level: 85 },
        { name: 'Postman', icon: '📬', level: 90 }
      ]
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  return (
    <section id="skills" className="skills-section" ref={skillsRef}>
      <div className="skills-container">
        <motion.h2 
          className="section-title"
          initial={{ opacity: 0, y: -20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          Habilidades
        </motion.h2>
        <motion.div 
          className="skills-grid"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
        >
          {skillsData.map((category, index) => (
            <motion.div 
              className="skill-category" 
              key={index}
              variants={itemVariants}
            >
              <h3 className="skill-category-title">{category.title}</h3>
              <div className="skill-list">
                {category.skills.map((skill, idx) => (
                  <motion.div 
                    className="skill-item" 
                    key={idx}
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <div className="skill-icon">{skill.icon}</div>
                    <div className="skill-name">{skill.name}</div>
                    <div className="skill-progress">
                      <div 
                        className="skill-progress-bar"
                        style={{ width: `${skill.level}%` }}
                      />
                    </div>
                    <div className="skill-level">{skill.level}%</div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Skills;
