import React from 'react';
import { motion } from 'framer-motion';
import { useTranslation } from 'react-i18next';
import './Hero.css';

const Hero = () => {
  const { t } = useTranslation();

  // Animaciones sin opacidad
  const imageVariants = {
    hidden: { y: 30 },
    visible: {
      y: 0,
      transition: {
        type: 'spring',
        stiffness: 80,
        damping: 15,
        duration: 1
      }
    }
  };

  const textVariants = {
    hidden: { y: 30 },
    visible: {
      y: 0,
      transition: {
        type: 'spring',
        stiffness: 80,
        damping: 15,
        duration: 1,
        delay: 0.2
      }
    }
  };

  const scrollButtonVariants = {
    hidden: { y: 20 },
    visible: {
      y: 0,
      transition: {
        type: 'spring',
        stiffness: 80,
        damping: 15,
        duration: 0.8,
        delay: 0.5
      }
    },
    hover: {
      y: 5,
      transition: {
        duration: 0.4,
        yoyo: Infinity
      }
    }
  };

  return (
    <div className="hero-container">
      <div className="hero-content">
        <motion.div
          className="hero-text"
          initial="hidden"
          animate="visible"
          variants={textVariants}
        >
          <h1 className="hero-name">{t('hero.greeting')}</h1>
          <h2 className="hero-title">{t('hero.title')}</h2>
          <p className="hero-description">{t('hero.description')}</p>
          <div className="hero-cta">
            <a href="#contact" className="cta-button primary">{t('hero.contact')}</a>
            <a href="#projects" className="cta-button secondary">{t('hero.projects')}</a>
          </div>
        </motion.div>

        <motion.div
          className="hero-image"
          initial="hidden"
          animate="visible"
          variants={imageVariants}
        >
          <img src="/assets/img/hero-image.png" alt="Diego Moreno" />
        </motion.div>
      </div>

      <motion.div
        className="scroll-down"
        initial="hidden"
        animate="visible"
        whileHover="hover"
        variants={scrollButtonVariants}
      >
        <a href="#about">
          <span>{t('hero.scrollDown')}</span>
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <line x1="12" y1="5" x2="12" y2="19"></line>
            <polyline points="19 12 12 19 5 12"></polyline>
          </svg>
        </a>
      </motion.div>
    </div>
  );
};

export default Hero;
