import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import navigationConfig from '../../config/navigationConfig';
import './Header.css';

const Header = ({ activeSection, isSidebarOpen, toggleSidebar }) => {
  const { t, i18n } = useTranslation();
  const [isDarkTheme, setIsDarkTheme] = useState(
    document.documentElement.classList.contains('dark-theme')
  );

  // Cambiar idioma
  const toggleLanguage = () => {
    const newLanguage = i18n.language === 'es' ? 'en' : 'es';
    i18n.changeLanguage(newLanguage);
    localStorage.setItem('preferredLanguage', newLanguage);
  };

  // Cambiar tema
  const toggleTheme = () => {
    const newTheme = !isDarkTheme;
    setIsDarkTheme(newTheme);
    document.documentElement.classList.toggle('dark-theme', newTheme);
    localStorage.setItem('preferredTheme', newTheme ? 'dark' : 'light');
  };

  // Desplazamiento suave a secciones
  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      
      // Si el menú está abierto en móvil, cerrarlo
      if (window.innerWidth < 768 && isSidebarOpen) {
        toggleSidebar();
      }
    }
  };

  return (
    <header className={`site-header ${isSidebarOpen ? 'sidebar-open' : ''}`}>
      <div className="header-container">
        <div className="logo-container">
          <a href="#home" onClick={() => scrollToSection('home')} className="logo">
            DM
          </a>
          
          <button 
            className={`menu-toggle ${isSidebarOpen ? 'active' : ''}`} 
            onClick={toggleSidebar}
            aria-label="Toggle navigation menu"
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
        
        <nav className={`main-nav ${isSidebarOpen ? 'open' : ''}`}>
          <ul className="nav-links">
            {navigationConfig.map(item => (
              <li key={item.id}>
                <a 
                  href={item.path}
                  onClick={(e) => {
                    e.preventDefault();
                    scrollToSection(item.id);
                  }}
                  className={activeSection === item.id ? 'active' : ''}
                >
                  {item.label[i18n.language] || item.label.en}
                </a>
              </li>
            ))}
          </ul>
          
          <div className="nav-actions">
            <button 
              className="language-toggle" 
              onClick={toggleLanguage}
              aria-label="Toggle language"
            >
              {i18n.language === 'es' ? 'EN' : 'ES'}
            </button>
            
            <button 
              className={`theme-toggle ${isDarkTheme ? 'dark' : 'light'}`} 
              onClick={toggleTheme}
              aria-label="Toggle theme"
            >
              <span className="theme-icon"></span>
            </button>
          </div>
        </nav>
      </div>
    </header>
  );
};

export default Header;
