import React, { useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import './Skills.css';
import { useTranslation } from 'react-i18next';

const Skills = () => {
  const { t } = useTranslation();
  const carouselRef = useRef(null);

  const skillsData = [
    { name: 'React', icon: '⚛️' },
    { name: 'JavaScript', icon: '🟨' },
    { name: 'TypeScript', icon: '🟦' },
    { name: 'HTML5', icon: '📄' },
    { name: 'CSS3', icon: '🎨' },
    { name: 'SASS', icon: '💅' },
    { name: 'Node.js', icon: '🟩' },
    { name: 'Express', icon: '🚀' },
    { name: 'MongoDB', icon: '🍃' },
    { name: 'Firebase', icon: '🔥' },
    { name: 'REST API', icon: '🌐' },
    { name: 'GraphQL', icon: '📊' },
    { name: 'Git', icon: '🐙' },
    { name: 'Webpack', icon: '📦' },
    { name: 'Docker', icon: '🐳' },
    { name: 'Jest', icon: '🧪' },
    { name: 'Figma', icon: '✏️' },
    { name: 'Postman', icon: '📬' },
    { name: 'PHP', icon: '🐘' },
    { name: 'WordPress', icon: '📰' },
    { name: 'Drupal', icon: '💧' },
    { name: 'MySQL', icon: '🛢️' },
    { name: '.NET', icon: '🔷' },
    { name: 'C#', icon: '🔶' },
  ];

  // Duplicamos los elementos para lograr el efecto de bucle infinito
  const duplicatedSkills = [...skillsData, ...skillsData];

  useEffect(() => {
    const carousel = carouselRef.current;
    if (!carousel) return;

    let scrollInterval;

    // Función para iniciar el desplazamiento automático
    const startScroll = () => {
      scrollInterval = setInterval(() => {
        if (carousel.scrollLeft >= (carousel.scrollWidth / 2 - 10)) {
          carousel.scrollLeft = 0;
        } else {
          carousel.scrollLeft += 1;
        }
      }, 20);
    };

    // Función para detener el desplazamiento
    const stopScroll = () => {
      clearInterval(scrollInterval);
    };

    // Iniciar el desplazamiento al cargar
    startScroll();

    // Pausar cuando el mouse está encima
    carousel.addEventListener('mouseenter', stopScroll);
    carousel.addEventListener('mouseleave', startScroll);

    // Limpiar eventos al desmontar
    return () => {
      stopScroll();
      carousel.removeEventListener('mouseenter', stopScroll);
      carousel.removeEventListener('mouseleave', startScroll);
    };
  }, []);

  return (
    <section className="skills-section" id="skills">
      <div className="skills-container">
        <motion.h2 
          className="section-title"
          initial={{ y: -20 }}
          whileInView={{ y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          {t('skills.title')}
        </motion.h2>

        <div className="skills-description">
          <motion.p 
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            {t('skills.description')}
          </motion.p>
        </div>

        <motion.div 
          className="skills-carousel-container"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8, delay: 0.3 }}
        >
          <div className="skills-carousel" ref={carouselRef}>
            {duplicatedSkills.map((skill, index) => (
              <motion.div 
                className="skill-item"
                key={index}
                whileHover={{ scale: 1.1, y: -5 }}
              >
                <div className="skill-icon">{skill.icon}</div>
                <div className="skill-name">{skill.name}</div>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Skills;
