import React from 'react';
import { useTranslation } from 'react-i18next';
import navigationConfig from '../../config/navigationConfig';
import './Footer.css';

const Footer = () => {
  const { t, i18n } = useTranslation();
  const currentYear = new Date().getFullYear();

  // Función para desplazamiento suave a secciones
  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <footer className="site-footer">
      <div className="footer-container">
        <div className="footer-content">
          <div className="footer-logo">
            <a href="#home" onClick={() => scrollToSection('home')}>
              <span className="logo-text">DM</span>
            </a>
            <p className="footer-tagline">{t('hero.description')}</p>
          </div>
          
          <div className="footer-nav">
            <h3>{t('nav.home')}</h3>
            <ul className="footer-links">
              {navigationConfig.map(item => (
                <li key={item.id}>
                  <a 
                    href={item.path}
                    onClick={(e) => {
                      e.preventDefault();
                      scrollToSection(item.id);
                    }}
                  >
                    {item.label[i18n.language] || item.label.en}
                  </a>
                </li>
              ))}
            </ul>
          </div>
          
          <div className="footer-contact">
            <h3>{t('contact.title')}</h3>
            <p>Email: info@diegomoreno.dev</p>
            <div className="social-links">
              <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">LinkedIn</a>
              <a href="https://github.com" target="_blank" rel="noopener noreferrer">GitHub</a>
              <a href="https://twitter.com" target="_blank" rel="noopener noreferrer">Twitter</a>
            </div>
          </div>
        </div>
        
        <div className="footer-bottom">
          <p>&copy; {currentYear} Diego Moreno. {t('footer.rights')}</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
