import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGlobe, faCode } from '@fortawesome/free-solid-svg-icons';
import { faGithub } from '@fortawesome/free-brands-svg-icons';
import './Projects.css';

// Importamos las imágenes (asegúrate de tener estas imágenes en tu carpeta public/assets/img)
const projectImages = {
  consum: '/assets/img/projects/consum.jpg',
  benidorm: '/assets/img/projects/benidorm.jpg',
  hm: '/assets/img/projects/hm.jpg',
  edificaciones: '/assets/img/projects/edificaciones.jpg',
  taskManager: '/assets/img/projects/task-manager.jpg',
  apiDotnet: '/assets/img/projects/api-dotnet.jpg',
  portfolio: '/assets/img/projects/portfolio.jpg',
  weatherApp: '/assets/img/projects/weather-app.jpg'
};

const Projects = () => {
  const { t } = useTranslation();
  const [filter, setFilter] = useState('all');

  // Datos de proyectos
  const projects = [
    {
      id: 1,
      title: 'Consum',
      category: 'drupal',
      image: projectImages.consum,
      description: 'Desarrollo de nueva página Web que permite información sobre artículos, tiendas, consultas de cupones, comprar productos.',
      detailedDescription: 'Participé en el desarrollo de la nueva web de Consum, implementando funcionalidades clave como el buscador de tiendas, la consulta de cupones y el catálogo de productos. Trabajé principalmente en el frontend y la integración con APIs internas.',
      technologies: ['Drupal 9', 'PHP', 'JavaScript', 'API REST', 'SASS'],
      liveUrl: 'https://www.consum.es',
      githubUrl: '',
      featured: true
    },
    {
      id: 2,
      title: 'Ayuntamiento de Benidorm',
      category: 'drupal',
      image: projectImages.benidorm,
      description: 'Multisite con noticias, información política, áreas municipales y eventos destacados.',
      detailedDescription: 'Desarrollé una plataforma multisite para el Ayuntamiento de Benidorm, con gestión de noticias, eventos, información de áreas municipales y servicios ciudadanos. El proyecto incluye un sistema de notificaciones y un calendario integrado.',
      technologies: ['Drupal 8', 'PHP', 'JavaScript', 'SASS', 'MySQL'],
      liveUrl: 'https://www.benidorm.org',
      githubUrl: '',
      featured: true
    },
    {
      id: 3,
      title: 'Hospitales HM',
      category: 'drupal',
      image: projectImages.hm,
      description: 'Sitio web con noticias de salud, coronavirus, investigación, premios, etc.',
      detailedDescription: 'Trabajé en la implementación del sistema de gestión de contenidos para Hospitales HM, con secciones personalizadas para noticias médicas, investigaciones y premios. Incluye un sistema de búsqueda avanzada y perfiles de médicos.',
      technologies: ['Drupal 7/8', 'PHP', 'JavaScript', 'CSS3', 'jQuery'],
      liveUrl: 'https://www.hmhospitales.com',
      githubUrl: '',
      featured: false
    },
    {
      id: 4,
      title: 'Edificaciones Caballero',
      category: 'wordpress',
      image: projectImages.edificaciones,
      description: 'Sitio para compra, venta y alquiler de edificios, con nuevos contenidos e imágenes.',
      detailedDescription: 'Desarrollé un sitio web personalizado para una inmobiliaria, incluyendo un sistema de filtrado de propiedades, formularios de contacto avanzados y un panel de administración para gestionar los inmuebles fácilmente.',
      technologies: ['WordPress', 'PHP', 'JavaScript', 'CSS', 'MySQL'],
      liveUrl: 'https://www.edificacionescaballero.es',
      githubUrl: '',
      featured: false
    },
    {
      id: 5,
      title: 'Gestión de Tareas',
      category: 'angular',
      image: projectImages.taskManager,
      description: 'Aplicación de gestión de tareas con autenticación y persistencia de datos.',
      detailedDescription: 'Aplicación completa para la gestión de tareas personales y de equipo, con funcionalidades de autenticación, asignación de responsables, fechas límite, etiquetas y categorías. Implementa notificaciones y un panel de estadísticas.',
      technologies: ['Angular', 'TypeScript', 'Bootstrap', 'Firebase', 'RxJS'],
      liveUrl: 'https://task-manager-dm.web.app',
      githubUrl: 'https://github.com/DM13250/task-manager',
      featured: true
    },
    {
      id: 6,
      title: 'API REST con C#',
      category: 'csharp',
      image: projectImages.apiDotnet,
      description: 'Desarrollo de API REST para sistema de gestión interna.',
      detailedDescription: 'API RESTful desarrollada con .NET Core para gestionar recursos internos de una empresa. Implementa autenticación JWT, validación de datos, operaciones CRUD completas y documentación con Swagger.',
      technologies: ['C#', '.NET Core', 'SQL Server', 'Swagger', 'Entity Framework'],
      liveUrl: 'https://api-demo.diegomoreno.dev',
      githubUrl: 'https://github.com/DM13250/dotnet-api',
      featured: false
    },
    {
      id: 7,
      title: 'Portfolio Personal',
      category: 'react',
      image: projectImages.portfolio,
      description: 'Sitio web personal para mostrar mi experiencia y proyectos.',
      detailedDescription: 'Desarrollo de mi portfolio personal utilizando React y las últimas tecnologías frontend. Incluye animaciones fluidas, modo oscuro/claro, internacionalización y diseño adaptable a todos los dispositivos.',
      technologies: ['React', 'JavaScript', 'Framer Motion', 'i18next', 'CSS Modules'],
      liveUrl: 'https://diegomoreno.dev',
      githubUrl: 'https://github.com/DM13250/portfolio-react',
      featured: true
    },
    {
      id: 8,
      title: 'Aplicación del Clima',
      category: 'react',
      image: projectImages.weatherApp,
      description: 'App de pronóstico del tiempo con geolocalización.',
      detailedDescription: 'Aplicación web que muestra el pronóstico del tiempo actual y de los próximos días. Utiliza geolocalización para detectar la ubicación del usuario y ofrece búsqueda por ciudades. Visualización de datos mediante gráficas interactivas.',
      technologies: ['React', 'OpenWeather API', 'ChartJS', 'CSS Grid', 'Axios'],
      liveUrl: 'https://weather-app-dm.web.app',
      githubUrl: 'https://github.com/DM13250/weather-app',
      featured: false
    }
  ];

  // Filtrado de proyectos
  const filteredProjects = filter === 'all' 
    ? projects 
    : projects.filter(project => project.category === filter);

  // Animaciones
  const containerVariants = {
    hidden: {},
    visible: {
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const projectVariants = {
    hidden: {
      y: 30
    },
    visible: {
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <section id="proyectos" className="projects">
      <div className="container projects-container">
        <div className="projects-title">
          <motion.h2
            initial={{ y: -10 }}
            whileInView={{ y: 0 }}
            viewport={{ once: true, amount: 0.2 }}
            transition={{ delay: 0.1, duration: 0.3 }}
          >
            {t('proyectos.titulo')}
          </motion.h2>
          <div className="underline"></div>
          <p className="section-subtitle">
            Estos son algunos de los proyectos más destacados en los que he trabajado. 
            Cada uno representa diferentes desafíos y soluciones técnicas implementadas.
          </p>
        </div>

        <div className="filter-buttons">
          <button
            className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
            onClick={() => setFilter('all')}
          >
            Todos
          </button>
          <button
            className={`filter-btn ${filter === 'drupal' ? 'active' : ''}`}
            onClick={() => setFilter('drupal')}
          >
            Drupal
          </button>
          <button
            className={`filter-btn ${filter === 'wordpress' ? 'active' : ''}`}
            onClick={() => setFilter('wordpress')}
          >
            WordPress
          </button>
          <button
            className={`filter-btn ${filter === 'angular' ? 'active' : ''}`}
            onClick={() => setFilter('angular')}
          >
            Angular
          </button>
          <button
            className={`filter-btn ${filter === 'react' ? 'active' : ''}`}
            onClick={() => setFilter('react')}
          >
            React
          </button>
          <button
            className={`filter-btn ${filter === 'csharp' ? 'active' : ''}`}
            onClick={() => setFilter('csharp')}
          >
            C#
          </button>
        </div>

        <motion.div 
          className="projects-grid"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.1 }}
        >
          {filteredProjects.map(project => (
            <motion.div 
              className="project-card" 
              key={project.id}
              variants={projectVariants}
            >
              <div className="project-image">
                <img src={project.image} alt={project.title} />
                {project.featured && <span className="featured-badge">Destacado</span>}
              </div>

              <div className="project-content">
                <h3>{project.title}</h3>
                <p className="project-description">{project.description}</p>
                <p className="project-detailed-description">{project.detailedDescription}</p>

                <div className="project-tech">
                  {project.technologies.map((tech, index) => (
                    <span key={index} className="tech-tag">{tech}</span>
                  ))}
                </div>

                <div className="project-links">
                  {project.liveUrl && (
                    <a href={project.liveUrl} target="_blank" rel="noopener noreferrer" className="project-link">
                      <FontAwesomeIcon icon={faGlobe} /> Sitio Web
                    </a>
                  )}

                  {project.githubUrl && (
                    <a href={project.githubUrl} target="_blank" rel="noopener noreferrer" className="project-link">
                      <FontAwesomeIcon icon={faGithub} /> GitHub
                    </a>
                  )}
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
