import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faTimes, faSun, faMoon, faReact } from '@fortawesome/free-solid-svg-icons';
import './Header.css';
// import { Helmet } from 'react-helmet';

// const SEO = ({ title, description, keywords }) => (
//   <Helmet>
//     <title>{title}</title>
//     <meta name="description" content={description} />
//     <meta name="keywords" content={keywords} />
//     <meta property="og:title" content={title} />
//     <meta property="og:description" content={description} />
//     <meta property="og:type" content="website" />
//     <meta name="twitter:card" content="summary" />
//     <meta name="twitter:title" content={title} />
//     <meta name="twitter:description" content={description} />
//   </Helmet>
// );

const Header = ({ activeSection, isDarkMode, toggleTheme }) => {
  const { t, i18n } = useTranslation();
  const [showMenu, setShowMenu] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  // Lista de idiomas disponibles
  const languages = [
    { code: 'es', label: 'ES' },
    { code: 'en', label: 'EN' }
  ];

  // Cambiar idioma
  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  // Controlar scroll
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 50;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [scrolled]);

  // Manejar clic en enlace de navegación
  const handleNavLinkClick = (sectionId) => {
    setShowMenu(false);
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    } else {
      console.error(`Elemento con ID ${sectionId} no encontrado`);
    }
  };

  // Animaciones
  const navVariants = {
    hidden: { opacity: 0, x: '100%' },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        type: 'spring',
        stiffness: 80,
        damping: 20
      }
    },
    exit: {
      opacity: 0,
      x: '100%',
      transition: {
        type: 'spring',
        stiffness: 100,
        damping: 20
      }
    }
  };

  const logoVariants = {
    normal: { scale: 1 },
    hover: { 
      scale: 1.05,
      transition: { 
        type: 'spring', 
        stiffness: 300, 
        damping: 10 
      }
    }
  };

  // Mapeo de IDs de sección a claves de traducción
  const navItems = [
    { id: 'inicio', translation: 'nav.inicio' },
    { id: 'sobre-mi', translation: 'nav.sobre-mi' },
    { id: 'habilidades', translation: 'nav.habilidades' },
    { id: 'proyectos', translation: 'nav.proyectos' },
    { id: 'contacto', translation: 'nav.contacto' }
  ];

  // Para depuración
  useEffect(() => {
    console.log('Sección activa en Header:', activeSection);
    
    // Verificar que todos los IDs de sección existen en el DOM
    navItems.forEach(item => {
      const element = document.getElementById(item.id);
      console.log(`Sección ${item.id}: ${element ? 'Encontrada' : 'No encontrada'}`);
    });
  }, [activeSection, navItems]);

  const scrollVariants = {
    hidden: { y: 50, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        type: "spring",
        stiffness: 100,
        damping: 15,
        duration: 0.8
      }
    }
  };

  const hoverScale = {
    scale: 1.05,
    transition: { 
      type: "spring", 
      stiffness: 300,
      damping: 10
    }
  };

  const ScrollIndicator = () => {
    const [scrollPercentage, setScrollPercentage] = useState(0);
    
    useEffect(() => {
      const handleScroll = () => {
        const windowHeight = document.documentElement.scrollHeight - document.documentElement.clientHeight;
        const scrolled = window.scrollY / windowHeight;
        setScrollPercentage(scrolled * 100);
      };
      
      window.addEventListener('scroll', handleScroll);
      return () => window.removeEventListener('scroll', handleScroll);
    }, []);
    
    return (
      <div className="scroll-progress" style={{width: `${scrollPercentage}%`}} />
    );
  };

  return (
    <header className={`header ${scrolled ? 'scrolled' : ''}`}>
      <ScrollIndicator />
      <div className="container header-container">
        <motion.div 
          className="logo" 
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <motion.a 
            href="#inicio" 
            variants={logoVariants}
            initial="normal"
            whileHover="hover"
            whileTap={{ scale: 0.9 }}
            onClick={(e) => {
              e.preventDefault();
              handleNavLinkClick('inicio');
            }}
          >
            <div className="logo-image-container">
              <img src="/logo-dm.png" alt="DM" className="logo-img" />
            </div>
            <span className="logo-text">Diego Moreno</span>
          </motion.a>
        </motion.div>

        <div className="nav-toggle">
          <motion.button 
            className="theme-toggle"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.1 }}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={toggleTheme}
            aria-label={isDarkMode ? 'Cambiar a modo claro' : 'Cambiar a modo oscuro'}
          >
            <FontAwesomeIcon icon={isDarkMode ? faSun : faMoon} />
          </motion.button>

          <motion.button 
            className="menu-toggle"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={() => setShowMenu(!showMenu)}
            aria-expanded={showMenu}
            aria-label="Menú de navegación"
          >
            <FontAwesomeIcon icon={showMenu ? faTimes : faBars} />
          </motion.button>
        </div>

        <AnimatePresence>
          {(showMenu || window.innerWidth > 768) && (
            <motion.nav 
              className={`nav ${showMenu ? 'show' : ''}`}
              variants={navVariants}
              initial="hidden"
              animate="visible"
              exit="exit"
            >
              <ul className="nav-links">
                {navItems.map((item) => (
                  <motion.li 
                    key={item.id}
                    initial={{ opacity: 0, y: -20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.2 + (0.1 * navItems.indexOf(item)) }}
                  >
                    <a 
                      href={`#${item.id}`}
                      onClick={(e) => {
                        e.preventDefault();
                        handleNavLinkClick(item.id);
                      }}
                      className={activeSection === item.id ? 'active' : ''}
                    >
                      {t(item.translation)}
                      {activeSection === item.id && (
                        <motion.div 
                          className="nav-indicator"
                          layoutId="navIndicator"
                          transition={{
                            type: 'spring',
                            stiffness: 200,
                            damping: 20
                          }}
                        />
                      )}
                    </a>
                  </motion.li>
                ))}
              </ul>

              <div className="language-switcher">
                {languages.map((lang) => (
                  <motion.button
                    key={lang.code}
                    onClick={() => changeLanguage(lang.code)}
                    className={i18n.language === lang.code ? 'active' : ''}
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    {lang.label}
                  </motion.button>
                ))}
              </div>
            </motion.nav>
          )}
        </AnimatePresence>
      </div>
    </header>
  );
};

export default Header;
