import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faGithub, 
  faLinkedin, 
  faTwitter,
  faInstagram
} from '@fortawesome/free-brands-svg-icons';
import './Contact.css';

const Contact = () => {
  const { t } = useTranslation();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });
  const [formStatus, setFormStatus] = useState(null);
  
  // Manejar cambios en el formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };
  
  // Manejar envío del formulario
  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Aquí se puede implementar la lógica para enviar el formulario
    // Por ahora solo simulamos un envío exitoso
    setFormStatus('success');
    
    // Reset del formulario después de 3 segundos
    setTimeout(() => {
      setFormData({
        name: '',
        email: '',
        message: ''
      });
      setFormStatus(null);
    }, 3000);
  };
  
  // Animaciones
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };
  
  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        type: 'spring',
        stiffness: 100
      }
    }
  };
  
  const socialVariants = {
    hidden: { scale: 0, opacity: 0 },
    visible: i => ({
      scale: 1,
      opacity: 1,
      transition: {
        delay: 0.3 + (i * 0.1),
        type: 'spring',
        stiffness: 120,
        damping: 10
      }
    })
  };

  return (
    <section id="contacto" className="contact-section">
      <div className="container">
        <motion.div
          className="section-title"
          initial={{ opacity: 0, y: -20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true, amount: 0.2 }}
          transition={{ delay: 0.2 }}
        >
          <h2>{t('contacto.titulo')}</h2>
          <div className="underline"></div>
          <p className="section-subtitle">
            ¿Interesado en trabajar juntos o quieres saber más sobre mis proyectos? 
            Envíame un mensaje y me pondré en contacto contigo lo antes posible.
          </p>
        </motion.div>
        
        <motion.div 
          className="contact-content"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.2 }}
        >
          <motion.div className="contact-info" variants={itemVariants}>
            <form className="contact-form" onSubmit={handleSubmit}>
              <div className="form-group">
                <input 
                  type="text" 
                  name="name" 
                  placeholder="Nombre" 
                  required 
                  value={formData.name}
                  onChange={handleChange}
                />
              </div>
              
              <div className="form-group">
                <input 
                  type="email" 
                  name="email" 
                  placeholder="Email" 
                  required 
                  value={formData.email}
                  onChange={handleChange}
                />
              </div>
              
              <div className="form-group">
                <textarea 
                  name="message" 
                  rows="5" 
                  placeholder="Mensaje" 
                  required
                  value={formData.message}
                  onChange={handleChange}
                ></textarea>
              </div>
              
              <motion.button 
                type="submit" 
                className="submit-btn"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                disabled={formStatus === 'success'}
              >
                {formStatus === 'success' ? 'Mensaje enviado' : 'Enviar Mensaje'}
              </motion.button>
              
              {formStatus === 'success' && (
                <motion.p 
                  className="form-success"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                >
                  ¡Gracias por tu mensaje! Te responderé lo antes posible.
                </motion.p>
              )}
            </form>
          </motion.div>
          
          <motion.div className="social-container" variants={itemVariants}>
            <h3>Encuéntrame en</h3>
            
            <div className="social-links">
              <motion.a 
                href="https://github.com/DM13250" 
                target="_blank"
                rel="noopener noreferrer"
                custom={0}
                variants={socialVariants}
                whileHover={{ y: -5 }}
              >
                <FontAwesomeIcon icon={faGithub} />
                <span>GitHub</span>
              </motion.a>
              
              <motion.a 
                href="https://www.linkedin.com/in/diegomoreno/" 
                target="_blank"
                rel="noopener noreferrer"
                custom={1}
                variants={socialVariants}
                whileHover={{ y: -5 }}
              >
                <FontAwesomeIcon icon={faLinkedin} />
                <span>LinkedIn</span>
              </motion.a>
              
              <motion.a 
                href="https://twitter.com/dmoreno_dev" 
                target="_blank"
                rel="noopener noreferrer"
                custom={3}
                variants={socialVariants}
                whileHover={{ y: -5 }}
              >
                <FontAwesomeIcon icon={faTwitter} />
                <span>Twitter</span>
              </motion.a>
              
              <motion.a 
                href="https://www.instagram.com/dmoreno_dev/" 
                target="_blank"
                rel="noopener noreferrer"
                custom={2}
                variants={socialVariants}
                whileHover={{ y: -5 }}
              >
                <FontAwesomeIcon icon={faInstagram} />
                <span>Instagram</span>
              </motion.a>
            </div>
            
            <div className="contact-details">
              <p>Email: <a href="mailto:dmr13250@gmail.com">dmr13250@gmail.com</a></p>
              <p>Teléfono: <a href="tel:+34662944881">+34 662944881</a></p>
              <p>Ubicación: Daimiel, Ciudad Real, España</p>
              <p>Disponibilidad: Tiempo completo / Remoto</p>
            </div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Contact;
