import React from 'react';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGithub, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import './Hero.css';

const Hero = () => {
  const { t } = useTranslation();

  // Variantes de animación
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3
      }
    }
  };

  const itemVariants = {
    hidden: { y: 50, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        type: 'spring',
        stiffness: 100,
        damping: 15
      }
    }
  };

  const socialVariants = {
    hidden: { scale: 0, opacity: 0 },
    visible: i => ({
      scale: 1,
      opacity: 1,
      transition: {
        delay: 1.5 + (i * 0.2),
        type: 'spring',
        stiffness: 200
      }
    })
  };

  return (
    <section id="inicio" className="hero-section">
      <div className="container hero-container">
        <motion.div 
          className="hero-content"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.h1 variants={itemVariants}>{t('hero.titulo')}</motion.h1>
          <motion.h2 variants={itemVariants}>{t('hero.subtitulo')}</motion.h2>
          <motion.p variants={itemVariants}>{t('hero.especialidad')}</motion.p>
        
          <motion.div className="hero-cta" variants={itemVariants}>
            <motion.a 
              href="#proyectos" 
              className="btn btn-primary"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              transition={{ type: 'spring', stiffness: 400, damping: 10 }}
            >
              {t('proyectos.titulo')}
            </motion.a>
          </motion.div>
          
          <motion.div className="social-links">
            <motion.a 
              href="https://github.com/DM13250" 
              target="_blank" 
              rel="noopener noreferrer"
              custom={0}
              variants={socialVariants}
              initial="hidden"
              animate="visible"
              whileHover={{ y: -5 }}
            >
              <FontAwesomeIcon icon={faGithub} />
            </motion.a>
            <motion.a 
              href="https://www.linkedin.com/in/diegomoreno/" 
              target="_blank" 
              rel="noopener noreferrer"
              custom={1}
              variants={socialVariants}
              initial="hidden"
              animate="visible"
              whileHover={{ y: -5 }}
            >
              <FontAwesomeIcon icon={faLinkedin} />
            </motion.a>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
