import React, { useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowDown } from '@fortawesome/free-solid-svg-icons';
import './Hero.css';

const Hero = () => {
  const { t } = useTranslation();

  // Animaciones con valores iniciales modificados para garantizar la visibilidad
  const imageVariants = {
    hidden: { opacity: 0.5, scale: 0.9 },
    visible: {
      opacity: 1,
      scale: 1,
      transition: {
        delay: 0.2,
        duration: 0.5
      }
    }
  };

  const textVariants = {
    hidden: { opacity: 0.5, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        delay: 0.3,
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  // Forzar visibilidad después de cargar el componente
  useEffect(() => {
    // Asegurar que el contenido sea visible incluso si la animación falla
    const timer = setTimeout(() => {
      const heroContent = document.querySelector('.hero-content');
      const heroImage = document.querySelector('.hero-image');
      
      if (heroContent) heroContent.style.opacity = '1';
      if (heroImage) heroImage.style.opacity = '1';
    }, 1000); // 1 segundo después de renderizar
    
    return () => clearTimeout(timer);
  }, []);

  const scrollDown = () => {
    const element = document.getElementById('sobre-mi');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="inicio" className="hero-section">
      <div className="container hero-container">
        <motion.div 
          className="hero-content"
          initial="hidden"
          animate="visible"
          style={{ opacity: 1 }} // Garantizar visibilidad
        >
          <motion.h1 variants={textVariants}>
            {t('hero.titulo')}
          </motion.h1>
          
          <motion.h2 variants={textVariants} className="hero-subtitle">
            {t('hero.subtitulo')}
          </motion.h2>
          
          <motion.p variants={textVariants} className="hero-speciality">
            {t('hero.especialidad')}
          </motion.p>
          
          <motion.div
            className="hero-cta"
            variants={textVariants}
          >
            <a 
              href="#proyectos" 
              className="btn btn-primary"
              onClick={(e) => {
                e.preventDefault();
                const element = document.getElementById('proyectos');
                if (element) element.scrollIntoView({ behavior: 'smooth' });
              }}
            >
              Ver Proyectos
            </a>
            
            <a 
              href="#contacto" 
              className="btn btn-outline"
              onClick={(e) => {
                e.preventDefault();
                const element = document.getElementById('contacto');
                if (element) element.scrollIntoView({ behavior: 'smooth' });
              }}
            >
              Contactar
            </a>
          </motion.div>
        </motion.div>
        
        <motion.div 
          className="hero-image"
          variants={imageVariants}
          initial="hidden"
          animate="visible"
          style={{ opacity: 1 }} // Garantizar visibilidad
        >
          <img src="/assets/img/hero-image.png" alt="Diego Moreno" />
        </motion.div>
        
        <motion.div
          className="scroll-down"
          onClick={scrollDown}
          initial={{ opacity: 0.5 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1 }}
          whileHover={{ y: 5 }}
          whileTap={{ scale: 0.95 }}
        >
          <FontAwesomeIcon icon={faArrowDown} />
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
