import React from 'react';
import { useTranslation } from 'react-i18next';
import './Footer.css';

const Footer = () => {
  const { t } = useTranslation();
  const currentYear = new Date().getFullYear();

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <footer className="footer">
      <div className="container">
        <div className="footer-content">
          <div className="footer-logo">
            <h3>Diego Moreno</h3>
            <p>Desarrollador Web</p>
          </div>
          
          <div className="footer-links">
            <a href="#inicio" onClick={(e) => { e.preventDefault(); scrollToSection('inicio'); }}>Inicio</a>
            <a href="#sobre-mi" onClick={(e) => { e.preventDefault(); scrollToSection('sobre-mi'); }}>Sobre MÃ­</a>
            <a href="#habilidades" onClick={(e) => { e.preventDefault(); scrollToSection('habilidades'); }}>Habilidades</a>
            <a href="#proyectos" onClick={(e) => { e.preventDefault(); scrollToSection('proyectos'); }}>Proyectos</a>
            <a href="#contacto" onClick={(e) => { e.preventDefault(); scrollToSection('contacto'); }}>Contacto</a>
          </div>
        </div>
        
        <div className="footer-bottom">
          <p>&copy; {currentYear} Diego Moreno. {t('footer.derechos')}</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
